/** * 冒泡排序 * @Author:zhenyuan * @Date:Created in 22:08 2018/6/12 */public class BubbleSort {    public static void main(String[] args){        int[] a = {2,3,1,6,4,36,7,5,56,43,12,14,50,88};        new BubbleSort().bubleSort(a);        for (int i=0;i<a.length;i++){            System.out.print(a[i]+" ");        }    }    public void bubleSort(int[] array){        int length = array.length;        int temp;        for (int i=0;i<length;i++){            for (int j=0;j<length-i-1;j++){                if (array[j]>array[j+1]){                    temp = array[j+1];                    array[j+1]=array[j];                    array[j]=temp;                }            }        }    }}