/** * 归并排序 * @Author:zhenyuan * @Date:Created in 22:05 2018/6/12 */public class MergeSort {    public static void main(String[] args){        int[] a = {2,3,1,6,4,36,7,5,56,43,12,14,50,88};        new MergeSort().mergeSort(a,0,a.length-1);        for (int i=0;i<a.length;i++){            System.out.print(a[i]+" ");        }    }    public void mergeSort(int[] array,int l,int h){        if (h>l){            int mid = l+(h-l)/2;            mergeSort(array,l,mid);            mergeSort(array,mid+1,h);            sort(array,l,h);        }        return ;    }    private void sort(int[] a,int l,int h){        int length = h-l+1, mid = l+(h-l)/2,index=0,l1=mid+1,low = l;        int[] aus = new int[length];        while (l<=mid&l1<=h){            if ((a[l]<a[l1])){ aus[index++]=a[l++];}            else {aus[index++]=a[l1++];}        }        while (l1<=h){            aus[index++]=a[l1++];        }        while (l<=mid){            aus[index++]=a[l++];        }        for (int i=0;i<length;i++){            a[i+low]=aus[i];        }    }}